class TestBean {
  DataBean data;
  int errorCode;
  String errorMsg;

  static TestBean fromMap(Map<String, dynamic> map) {
    if (map == null) return null;
    TestBean testBeanBean = TestBean();
    testBeanBean.data = DataBean.fromMap(map['data']);
    testBeanBean.errorCode = map['errorCode'];
    testBeanBean.errorMsg = map['errorMsg'];
    return testBeanBean;
  }

  Map toJson() => {
        "data": data,
        "errorCode": errorCode,
        "errorMsg": errorMsg,
      };
}

